<template>
  <div id="footer">
    <ul>
      <li v-for="(item, index) in cont" @click="focus(index)" :class="{ active: isActive == index }">
        <router-link v-bind:to="item.path">
          <i class="icon" v-bind:class="item.img"></i>
          {{item.name}}
        </router-link>
      </li>
    </ul>
  </div>
</template>

<script>
  export default {
    name: 'footer',
    data () {
      return {
        msg: '这是底部的导航栏',
        cont: [
          {
            name: '首页',
            img: 'icon-home',
            path: '/'
          },
          {
            name: '展厅',
            img: 'icon-exhibition',
            path: '/exhibition'
          },
          {
            name: '爱车',
            img: 'icon-car',
            path: '/car'
          },
          {
            name: '服务',
            img: 'icon-service',
            path: '/service'
          },
          {
            name: '我的',
            img: 'icon-my',
            path: '/my'
          }
        ],
        isActive: 0
      }
    },
    methods: {
      focus: function (index) {
        if (index > this.isActive) {
          this.$store.dispatch('ToggleDirect')
        } else {
          this.$store.dispatch('ToggleDirect')
        }
        this.isActive = index
      }
    }
  }
</script>

